var itemList = msg.payload || [];
var blockSize = 50;
var defaultThingType = "Item"
var req=[];
var updateList = [];

//node.warn('procesing payload');
var generateJson = function(thing){
    if(!!thing){
        var updJson = { 
                "thingTypeCode" : thing.thingTypeCode || defaultThingType,
                "serialNumber":""+thing.serialNumber,
                "timestamp":(new Date()).getTime()
        };
        if(thing.udfs){
            for(var key in thing.udfs){
                updJson[key] = thing.udfs[key].value || "";
                delete thing.udfs.Retail_Product_SKU;
                delete thing.udfs.Retail_Product_SKUOriginal;
            }    
        }

        req.push(updJson);
        if(req.length >= blockSize){
            updateList.push({ payload:req });
            req=[];
        }
    }
}
//node.warn(itemList);
for (var i=0; i<itemList.length; i++){
    if(itemList[i].hasOwnProperty('serialNumber')){
        generateJson(itemList[i]);
    }
}
//node.warn('updateList');

if(req.length > 0){
    updateList.push({ payload:req});
    req=[];
}
if(updateList.length === 0){
    return null;
}
//node.warn(updateList);

return [updateList];

