var savedVerfied = msg.payload;
var originalPayload = msg.originalPayload;
var asn = "";
var serial = originalPayload.tag_id;
var brand = originalPayload.brand;
var date="";
var destination = "";
if(originalPayload.asn && originalPayload.status){
    asn = originalPayload.asn + "_" + originalPayload.status;
}
// node.warn(asn);
// node.warn(serial);
// node.warn("verify asn")
//node.warn(originalPayload);
// node.warn(savedVerfied);

if(savedVerfied.hasOwnProperty(asn)){
    var epcList = savedVerfied[asn].epcList;

    date = new Date(parseInt(originalPayload.event_date));
    date = date.getFullYear()+
          "/"+(date.getMonth()+1)+
          "/"+date.getDate()+
          " "+date.getHours()+
          ":"+date.getMinutes()+
          ":"+date.getSeconds();

    if(originalPayload["status"] !== "packing"){
        destination = originalPayload["destination"];
    }

    savedVerfied[asn]["asn"] = originalPayload["asn"];
    savedVerfied[asn]["source"] = originalPayload["source"];
    savedVerfied[asn]["destination"] = destination;
    savedVerfied[asn]["status"]= originalPayload["status"];
    savedVerfied[asn]["event_date"] = date;
    savedVerfied[asn]["event_remarks"] = originalPayload["event_remarks"];
    savedVerfied[asn]["event_ref_id"] = originalPayload["event_ref_id"];


    for(var i=0; i<epcList.length; i++){
        if(epcList[i].serial_number === serial){
            epcList[i].check_flag = true;
            if(brand){
                epcList[i].validated = true;
                epcList[i]["original_location"] =  originalPayload["original_location"];
                epcList[i]["store_id"] =  originalPayload["store_id"];
                epcList[i]["process_status"] =  originalPayload["process_status"];
                //epcList[i]["sku"] =  originalPayload["sku"];
                epcList[i]["departmentid"] =  originalPayload["departmentid"];
                epcList[i]["brand"] =  originalPayload["brand"];
                epcList[i]["sku"] =  originalPayload["sku"];

            }
        }
    }
    savedVerfied[asn].epcList = epcList;
    msg.payload = savedVerfied;
}

return msg;

