                "thingTypeCode" : thing.thingTypeCode || defaultThingType,
                "serialNumber":""+thing.serialNumber,
                "timestamp":(new Date()).getTime()
        };
        if(thing.udfs){
            for(var key in thing.udfs){
                updJson[key] = thing.udfs[key].value || "";
            }    
        }

        req.push(updJson);
        if(req.length >= blockSize){
            updateList.push({ payload:req });
            req=[];
        }
    }
}
