var headers = msg.req.headers;

//checking if the api_key is correct, otherwise response is Unauthorized
if(!headers.apikey || headers.apikey != 'WYMOJXX8QZ4TBAWS'){
    node.warn("Unauthorized transaction, wrong credentials. ");
    msg.payload = {"response":"Unauthorized"};
    msg.statusCode = 401;
    return [msg,null];
}
else{
    node.warn("Authorized Tagging transaction, proceeding... ");

    var splChars = "*|,\":<>[]{}`\';()@&$#%/?!";
    results = msg.payload;
    for(var j=0;j < results.length;j++){
        for(var i=0;i < results[j].serialNumber.length;i++){
            if(splChars.indexOf(results[j].serialNumber.charAt(i)) != -1){
                msg.payload = {"response":"Invalid QR code"};
                msg.statusCode = 400;
            }
        }
        if(results[j].serialNumber.length < 18){
            msg.payload = {"response":"Invalid QR code"};
            msg.statusCode = 400;
        }
        if(results[j].udfs.Material){
            var Material = results[j].udfs.Material.value;
            if(Material.length > 18 || Material.length < 4 ){
                msg.payload = {"response":"Invalid MATERIAL code. Please review."};
                msg.statusCode = 400;
            }
        }

    }
    msg.payloadOriginal=msg.payload;
    return msg;
}

