// Function to generate filename with sequence
function generateFilename(baseFilename, sequenceNumber, fileExtension) {
    return baseFilename + (sequenceNumber > 0 ? sequenceNumber : '') + fileExtension;
}

// Check file size and generate a new file if needed
var filename = generateFilename(baseFilename, sequenceNumber, fileExtension);
while (fs.existsSync(filename) && fs.statSync(filename).size > 20 * 1024 * 1024) { // 20 MB size check
