var baseFilename = initialTime + '.txt';

var maxFileSize = 1024 * 1024; // 1MB in bytes
var sequence = flow.get('sequence') || 1; // Get the current sequence number, default to 1
var currentSize = flow.get('currentSize') || 0; // Get the current file size tracked, default to 0

// Generate the filename based on the current sequence
var filename = basePath + initialTime + '_' + sequence + '.txt';

// Assuming msg.payload contains the data to write, update the currentSize
var dataSize = Buffer.byteLength(msg.payload, 'utf8');
currentSize += dataSize;

// Check if the currentSize exceeds 1MB
if (currentSize > maxFileSize) {
    sequence++; // Increment the sequence number
    filename = basePath + initialTime + '_' + sequence + '.txt'; // Generate new filename
    currentSize = dataSize; // Reset the size for the new file
}

// Store the updated sequence and size in flow context
flow.set('sequence', sequence);
flow.set('currentSize', currentSize);

