    return [msg, null];
}

if (!Array.isArray(results)) {
    node.warn("Payload body should be an array");
    msg.payload = {"response": "Bad request. Payload body should be an array"};
    msg.statusCode = 400;
    return [msg, null];
}

// Checking if the api_key is correct, otherwise response is Unauthorized
if (!headers.apikey || headers.apikey != 'WYMOJXX8QZ4TBAWS') {
    node.warn("Unauthorized transaction, wrong credentials.");
    msg.payload = {"response": "Unauthorized"};
    msg.statusCode = 401;
    return [msg, null];
} else {
    node.warn("Authorized Tagging transaction, proceeding...");

    node.warn(results);

    var splChars = "*|,\":<>[]{}`\';()@&$#%/?!";
    for (var j = 0; j < results.length; j++) {
        var serialNumber = results[j].serialNumber;
        var material = results[j]?.udfs?.Material?.value;

        if (!serialNumber || typeof serialNumber !== 'string') {
            msg.payload = {"response": "Invalid QR code"};
            msg.statusCode = 400;
            return [msg, null];
        }

        for (var i = 0; i < serialNumber.length; i++) {
            if (splChars.indexOf(serialNumber.charAt(i)) !== -1) {
                msg.payload = {"response": "Invalid QR code"};
                msg.statusCode = 400;
                return [msg, null];
            }
        }

        if (serialNumber.length < 18) {
            msg.payload = {"response": "Invalid QR code"};
            msg.statusCode = 400;
            return [msg, null];
        }

        if (!material || material.length > 18) {
            msg.payload = {"response": "Bad request. Invalid MATERIAL code."};
            msg.statusCode = 400;
            return [msg, null];
        }
    }

    msg.payloadOriginal = results;
    msg.payload = results;
    msg.destinationFilename = results[0].serialNumber + "/pallet_" + results[0].serialNumber + ".png";
    msg.newDirectory = "/transfer/" + results[0].serialNumber;
    return msg;
}

