// Function to check for special characters
function containsSpecialChars(str) {
    var splChars = "*|,\":<>[]{}`\';()@&$#%/?!";
    for (var i = 0; i < str.length; i++) {
        if (splChars.indexOf(str.charAt(i)) !== -1) {
            return true;
        }
    }
    return false;
}

// Function to check if a value is non-empty and not null
function isValidValue(value) {
    return typeof value === 'string' && value.trim() !== '';
}

// Function to validate UDFs fields
function validateUDFs(udfs) {
    const udfsFields = ['Batch', 'Material', 'Qty', 'User'];
    for (let field of udfsFields) {
        const fieldValue = udfs[field] ? udfs[field].value : undefined;
        if (!fieldValue || !isValidValue(fieldValue)) {
            return false;
        }
    }
    return true;
}

// Validate the received request
var headers = msg.req.headers;

// Check if the API key is correct
if (!headers.apikey || headers.apikey != 'WYMOJXX8QZ4TBAWS') {
    node.warn("Unauthorized transaction, wrong credentials.");
    msg.payload = { "response": "Unauthorized" };
    msg.statusCode = 401;
    return [msg, null];
} else {
    node.warn("Authorized reTagging transaction, proceeding...");
    let results = msg.payload;

    // Validate that all serialNumbers are the same
    const allSameSerialNumbers = results.every(item => item.serialNumber === results[0].serialNumber);
    if (!allSameSerialNumbers) {
        msg.payload = { "response": "Different QR codes are not accepted." };
        msg.statusCode = 400;
        return msg;
    }

    for (let result of results) {
        // Check for special characters in serialNumber
        if (containsSpecialChars(result.serialNumber) || result.serialNumber.length < 18) {
            msg.payload = { "response": "Invalid QR code" };
            msg.statusCode = 400;
            return msg;
        }

        // Validate Material code length
        if (result.udfs.Material && (result.udfs.Material.value.length > 18 || result.udfs.Material.value.length < 4)) {
            msg.payload = { "response": "Invalid MATERIAL code. Please review." };
            msg.statusCode = 400;
            return msg;
        }

        // Validate UDFs
        if (!validateUDFs(result.udfs)) {
            msg.payload = { "response": "Invalid UDFs. Missing or empty values detected." };
            msg.statusCode = 400;
            return msg;
        }
    }

    msg.payloadOriginal = msg.payload;
    return msg;
}

